import {Component} from 'react'

import {Oval} from 'react-loader-spinner'

import Header from '../Header'
import './index.css'

const sortbyOptions = [
  {
    optionId: 'PRICE_HIGH',
    displayText: 'Price (High-Low)',
  },
  {
    optionId: 'PRICE_LOW',
    displayText: 'Price (Low-High)',
  },
]

class Products extends Component {
  state = {
    data: [],
    status: 'INITIAL',
    categories: [],
    sortBy: 'id',
    order: 'ASC',
    category: '',
    selectedCategory: '',
    searchInput: '',
    activeOptionId: sortbyOptions[0].optionId,
  }

  componentDidMount() {
    this.getAllCategories()
  }

  getAllCategories = async () => {
    this.setState({status: 'LOADING'})
    const answer = await fetch(`http://localhost:3001/categories`)
    if (answer.ok) {
      const data = await answer.json()
      this.setState({categories: data})
    }
    this.getAllProducts()
  }

  getAllProducts = async () => {
    const {sortBy, searchInput, order, category} = this.state

    const answer = await fetch(
      `http://localhost:3001/items?sort_by=${sortBy}&${order}&search_q=${searchInput}&category_id=${category}`,
    )
    if (answer.ok) {
      const data = await answer.json()
      this.setState({data, status: 'SUCCESS'})
    } else {
      this.setState({status: 'FAILURE'})
    }
  }

  onCategoryChange = event => {
    const {categories} = this.state
    const element = categories.filter(each => each.name === event.target.value)

    this.setState(
      {
        selectedCategory: event.target.value,
        category: element[0].id,
      },
      () => {
        this.getAllProducts()
      },
    )
  }

  renderLoading = () => (
    <div className="loader">
      <Oval
        height="50"
        width="50"
        color="#4fa94d"
        ariaLabel="oval-loading"
        wrapperStyle={{}}
        wrapperClass=""
      />
    </div>
  )

  renderSuccess = () => {
    const {data, categories, category, selectedCategory} = this.state
    console.log(data, categories, category)
    return (
      <div className="products-container">
        <div className="Category-container">
          <form>
            <label htmlFor="category"> Select Category: </label>
            <select
              onChange={this.onCategoryChange}
              value={selectedCategory}
              className="category"
              id="category"
            >
              {categories.map(each => (
                <option value={each.name}>{each.name}</option>
              ))}
            </select>
          </form>
        </div>
      </div>
    )
  }

  renderProducts = () => {
    const {status} = this.state

    switch (status) {
      case 'LOADING':
        return this.renderLoading()
      case 'SUCCESS':
        return this.renderSuccess()
      case 'FAILURE':
        return this.renderFailure()

      default:
        return null
    }
  }

  render() {
    const {data} = this.state

    return (
      <div>
        <Header />
        {this.renderProducts()}
      </div>
    )
  }
}

export default Products
